import { Rule } from 'eslint';
import * as ESTree from 'estree';

export default {
  create(context: Rule.RuleContext) {
    return {
      "TSBooleanKeyword": (node: ESTree.Node) => {
        context.report({ message: `Don't use 'boolean' as a type. Use 'bool' instead.`, node: node });
      },
    }
  }
};
