// TODO: return an error when rest parameters are used.
//
// Example:
//
// ```
// // this is fine.
// function doSomething(foo: Array<i32>) {}
//
// // this is not.
// function doSomething(...foo: i32) {}
// ```

import { Rule } from 'eslint';
import * as ESTree from 'estree';

export default {
  create(context: Rule.RuleContext) {
    return {
      "RestElement": (node: ESTree.Node) => {
        context.report({ message: 'rest params are not supported.', node: node });
      },
    }
  }
};
