// TODO: return an error when a spread is used.
//
// Example:
//
// ```
// // this is fine.
// let numbers = [1, 2, 3, 4, 5];
// doSomething(numbers);
//
// // this is not.
// let numbers = [1, 2, 3, 4, 5];
// doSomething(...numbers);
// ```
import { Rule } from 'eslint';
import * as ESTree from 'estree';

export default {
  create(context: Rule.RuleContext) {
    return {
      "SpreadElement": (node: ESTree.Node) => {
        context.report({ message: 'spreads are not supported.', node: node });
      },
    }
  }
};
