import rule from '../../lib/rules/no-boolean';
import { RuleTester } from 'eslint';

const ruleTester = new RuleTester({
  parser: require.resolve('@typescript-eslint/parser'),
  parserOptions: {
    sourceType: 'module',
    ecmaVersion: 6,
  }
});

ruleTester.run('no-boolean', rule, {
  valid: [
    'let foo: bool = false;',
    'function foo(a: bool): u16',
    'function foo(a: u16): bool',
  ],
  invalid: [
    { code: 'let foo: boolean = false;', errors: [{ message: `Don't use 'boolean' as a type. Use 'bool' instead.` }] },
    { code: 'function foo(a: boolean): u16', errors: [{ message: `Don't use 'boolean' as a type. Use 'bool' instead.` }] },
    { code: 'function foo(a: u16): boolean', errors: [{ message: `Don't use 'boolean' as a type. Use 'bool' instead.` }] },
  ]
});
