import rule from '../../lib/rules/no-unsupported-keyword';
import { RuleTester } from 'eslint';

const ruleTester = new RuleTester({
  parser: require.resolve('@typescript-eslint/parser'),
  parserOptions: {
    sourceType: 'module',
    ecmaVersion: 6,
  }
});

ruleTester.run('no-unsupported-keyword', rule, {
  valid: [],
  invalid: [
    { code: 'let foo: undefined;', errors: [{ message: "undefined is not supported." }] },
    { code: 'function foo(): never', errors: [{ message: "never is not supported." }] },
    { code: 'function foo(a: any)', errors: [{ message: "any is not supported." }] },
  ]
});
